INSERT INTO dbo.Reports2
([ReportType],[ReportName],[Query],[CreatedBy],[DateCreated],[Comment])
VALUES
(1,'Computers - No Client - By AD Site','SELECT DISTINCT ResourceID, Name AS ComputerName,Domain,IsAssigned, IsClient,ClientType, IsObsolete, IsActive, IsDecommissioned, IsBlocked, IsAlwaysInternet, IsInternetEnabled FROM dbo.v_FullCollectionMembership WHERE IsAssigned=1 AND IsClient=0 AND Name NOT LIKE ''%Unknown Computer)'' ORDER BY Name','sccmadmin','2016-12-08 01:23:00','Computers - No Client'),
(1,'Computers - Physical - Windows 10','SELECT dbo.v_R_System.Name0 AS Computer, dbo.v_R_System.ResourceID, dbo.v_R_System.Client_Version0 AS ClientVersion, dbo.v_GS_COMPUTER_SYSTEM.Manufacturer0 AS MFR, dbo.v_GS_COMPUTER_SYSTEM.Model0 AS Model, dbo.v_GS_OPERATING_SYSTEM.Caption0 AS OS, dbo.v_GS_OPERATING_SYSTEM.CSDVersion0 AS ServicePack, dbo.v_GS_COMPUTER_SYSTEM.Domain0 AS Domain FROM dbo.v_R_System INNER JOIN dbo.v_GS_OPERATING_SYSTEM ON dbo.v_R_System.ResourceID = dbo.v_GS_OPERATING_SYSTEM.ResourceID LEFT OUTER JOIN dbo.v_GS_COMPUTER_SYSTEM ON dbo.v_R_System.ResourceID = dbo.v_GS_COMPUTER_SYSTEM.ResourceID WHERE (dbo.v_R_System.Client0 = 1) AND (dbo.v_R_System.Obsolete0 = 0) AND (NOT (dbo.v_R_System.Name0 LIKE ''%VM%'')) AND (NOT (dbo.v_GS_COMPUTER_SYSTEM.Model0 LIKE ''%Virtual%'')) AND (dbo.v_GS_COMPUTER_SYSTEM.Domain0 IS NOT NULL) AND (dbo.v_GS_OPERATING_SYSTEM.Caption0 LIKE ''%Windows 10%'')','sccmadmin','2016-12-08 01:23:00','Physical computers running on Windows 10'),
(1,'Updates - Non-Compliant Summary','SELECT CI_ID AS UID, BulletinID, ArticleID, SeverityName, NumTotal AS Scanned, NumMissing AS Missing, NumPresent AS Installed, NumNotApplicable AS NotReqd, NumUnknown AS Unknown, PercentCompliant AS Compliant, CASE WHEN IsExpired=1 THEN ''YES'' ELSE ''NO'' END AS Expired, CASE WHEN IsSuperseded=1 THEN ''YES'' ELSE ''NO'' END AS Superseded,CASE WHEN IsDeployed=1 THEN ''YES'' ELSE ''NO'' END AS Deployed,LastStatusTime FROM dbo.vSMS_SoftwareUpdate WHERE PercentCompliant < 100RDER BY PercentCompliant','sccmadmin','2016-12-08 01:23:00','Summary of Updates not fully deployed')
